mapper.select:
SELECT * FROM study JOIN gather ON ga_st_num = st_num LEFT JOIN tag_register ON tr_st_num = st_num WHERE ga_title LIKE CONCAT('%', #{cri.search}, '%')
AND st_state = 1
AND st_re_name = #{cri.region}
AND st_num IN ( SELECT tr_st_num FROM tag_register WHERE tr_name IN
#{tag}
)
GROUP BY st_num ORDER BY st_num DESC LIMIT #{cri.pageStart}, #{cri.perPageNum}

select * from gather 
join member on me_id = ga_me_id
where ga_st_num = #{st_num}


select count(*) from (
	select * from gather
		JOIN study ON st_num = ga_st_num
		left join tag_register on tr_st_num = st_num
		WHERE st_name LIKE CONCAT('%',#{cri.search},'%')

				AND st_re_name = #{cri.region}


			and tr_name in 

			        #{tag}


		group by ga_num
) as t

SELECT ga_st_num FROM want 
join gather on ga_num = wa_ga_num
where wa_state=1 and wa_me_id = #{me_id}

select * from study_member
join study on st_num = sm_st_num
where sm_me_id = #{me_id} and sm_st_num =#{st_num}
