mapper.update:
update member
set
me_authority = #{me_authority}
where
me_id = #{me_id}

UPDATE member
SET me_name = #{m.me_name},
me_pw = #{m.me_pw},
me_email = #{m.me_email}
WHERE me_id = #{m.me_id}

UPDATE member
SET me_profile = #{m.me_profile}
WHERE me_id = #{m.me_id}

UPDATE
    member
SET
    me_pw = #{me_pw}
WHERE
    me_id = #{me_id}
    AND me_email = #{me_email}
