mapper.select:
		SELECT *
		FROM pay
		WHERE pa_me_id = #{memberId}
		ORDER BY pa_date DESC 
 		limit #{cri.pageStart}, #{cri.perPageNum}

select count(*)
from pay
where pa_me_id = #{memberId}

SELECT pa_order_id
FROM pay
WHERE pa_me_id = #{memberId}

SELECT *
FROM pay
WHERE pa_order_id = #{paOrderId}

select count(*)
from reservation
where re_me_id = #{memberId}

select * from study WHERE st_me_id = #{memberId}
AND st_state = 1
ORDER BY st_num DESC limit #{cri.pageStart}, #{cri.perPageNum}

SELECT * FROM study_member join study on sm_st_num = st_num WHERE sm_me_id = #{memberId}
AND st_state = 1
ORDER BY st_num DESC limit #{cri.pageStart}, #{cri.perPageNum}

  		select count(*)
		from board
		where bo_me_id = #{memberId}

select ga_num, ga_title, ga_me_id, me_name, st_num, st_image, st_re_name, st_now_people, st_total_people, st_state from study join gather on ga_st_num = st_num join member on me_id = st_me_id WHERE ga_me_id = #{memberId}
AND st_state = 1
ORDER BY ga_num DESC limit #{gatherCri.pageStart}, #{gatherCri.perPageNum}

select * 
	from gather
	where ga_st_num = #{st_num}
