mapper.select:
SELECT * FROM notice WHERE no_title LIKE CONCAT('%', #{cri.search}, '%')
AND no_nt_num = #{cri.type}
ORDER BY no_num DESC no_register_date DESC no_views ASC LIMIT #{cri.pageStart}, #{cri.perPageNum}

select count(*) from (
	select * from notice
	where no_title LIKE CONCAT('%', #{cri.search}, '%')
	
		AND no_nt_num = #{cri.type}
	
)tmp
